mapper.update:
update oa_account
set username = #{username,jdbcType=VARCHAR},
  account = #{account,jdbcType=VARCHAR},
  `password` = #{password,jdbcType=VARCHAR},
  memo = #{memo,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, username, account, `password`, memo
from oa_account
where id = #{id,jdbcType=INTEGER}
